import React from "react";
import complete from "../image/complete.svg"
import error from "../image/error.svg"

const InfoTooltip = ({onClose, isOpen}) => {
const [isComplete, setIsComplete] = React.useState(false)
const [title, setTitle] = React.useState("good");
const [imgSrc, setImgSrc] = React.useState(complete);

 const setPopupContent = () => {
   isComplete ? setTitle("good") & setImgSrc(complete)
   : setTitle("bad") & setImgSrc()
 }

  return ( 
    <div
    className={`popup popup_type_info 
      ${isOpen ? `popup_opened` : ""}`}
  >
    <fieldset className="popup__container">
      <button
        onClick={onClose}
        type="button"
        className={`popup__close popup__close_type_info`}
      />
      <img src={imgSrc}></img>
      <h2 className="popup__title">{title}</h2>
    </fieldset>
  </div>

  );
}
 
export default InfoTooltip;
