import { useLocation } from "react-router-dom";
import React from "react";
import { NavLink } from "react-router-dom";

function Header() {
 const [headerContainer, setHeaderContainer] = React.useState("")
 const location =    useLocation();

  function handleHeaderChange () {


    switch (location.pathname) {
      case '/login':
        setHeaderContainer( <NavLink className={"header__nav-link"} style={{color: "white"}}
          to="/register">Регистрация</NavLink>);
        break;
      case '/register':
        setHeaderContainer( <NavLink className={"header__nav-link"} style={{color: "white"}}
          to="/login">Войти</NavLink>);
        break;
      case '/main':
        setHeaderContainer(<div><button></button> <div style={{color: "white", margin: 0}}>мой имейл</div></div> ) ;
        break;
      default:
        alert( "Нет таких значений" );
    }
    // switch (location.pathname) {
    //     case '/login':
    //       // setHeaderContainer(<h1>Зарегистрироваться</h1>) ;
    //       console.log('/login')
    //     case '/register':
    //       console.log("on register")
    //       // setHeaderContainer(<div>Войти</div>) ;
    //     case '/main':
    //       console.log("on main")
    //       // setHeaderContainer(<div><button></button> <div>мой имейл</div></div> ) ;

    //   }
  }


  React.useEffect(() => {
    handleHeaderChange();
  }, [location.pathname])

  return (
    <header className="header">
      <div className="header__logo" />
      {headerContainer}
    </header>
  );
}

export default Header;
