import React from "react";
import { NavLink } from "react-router-dom";

const Register = ({ onRegister }) => {
    const [email, setEmail] = React.useState("");
    const [password, setPassword] = React.useState("");

  function handleEmailChange(e) {
    setEmail(e.target.value);
  }

  function handlePasswordChange(e) {
    setPassword(e.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();
    onRegister({
        email,
        password
    });
  }

  return (
    <form onSubmit={handleSubmit} className="register">
      <h1 className="register__title">Регистрация</h1>
      <input className="register__input" value={email || ""} onChange={handleEmailChange} placeholder="email"></input>
      <input className="register__input" value={password || ""} onChange={handlePasswordChange} placeholder="Пароль"></input>
      <button className="register__submitBtn">Зарегистрироваться</button>
      <NavLink className="register__label" to="/login">
        Уже зарегистрированны? Войти
      </NavLink>
    </form>
  );
};

export default Register;
