import { useLocation } from "react-router-dom";
import React from "react";

function Header() {
 const [headerContainer, setHeaderContainer] = React.useState("")
 const location =    useLocation();

  function handleHeaderChange () {


    switch (location.pathname) {
      case '/login':
        setHeaderContainer(<h1 style={{color: "white", margin: 0}}>Зарегистрироваться</h1>);
        break;
      case '/register':
        setHeaderContainer(<a href="/" style={{color: "white", margin: 0}}>Войти</a>) ;
        break;
      case '/main':
        setHeaderContainer(<div><button></button> <div style={{color: "white", margin: 0}}>мой имейл</div></div> ) ;
        break;
      default:
        alert( "Нет таких значений" );
    }
    // switch (location.pathname) {
    //     case '/login':
