    //       console.log('/login')
    //     case '/register':
    //       console.log("on register")
    //       // setHeaderContainer(<div>Войти</div>) ;
    //     case '/main':
    //       console.log("on main")
    //       // setHeaderContainer(<div><button></button> <div>мой имейл</div></div> ) ;

    //   }
  }


  React.useEffect(() => {
    handleHeaderChange();
  }, [location.pathname])

  return (
    <header className="header">
      <div className="header__logo" />
      {headerContainer}
    </header>
  );
}

export default Header;
