import PopupWithForm from "./PopupWithForm";
import React from "react";
import { CurrentUserContextObj } from "../contexts/CurrentUserContext";

const EditProfilePopup = ({ isOpen, isLoading, onClose, onUpdateUser }) => {
  const [name, setName] = React.useState("");
  const [description, setDescription] = React.useState("");
  const currentUser = React.useContext(CurrentUserContextObj);

  function handleNameChange(e) {
    setName(e.target.value);
  }

  function handleDescriptionChange(e) {
    setDescription(e.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();

    onUpdateUser({
      name: name,
      about: description,
    });
  }
  React.useEffect(() => {
    setName(currentUser.name);
    setDescription(currentUser.about);
  }, [isOpen, currentUser]);

  return (
    <PopupWithForm
      onSubmit={handleSubmit}
      isOpen={isOpen}
      onClose={onClose}
      name="edit-profile"
      title="Редактировать профиль"
      buttonText={!isLoading ? "Сохранить" : "Сохранение..."}
    >
      <label className="popup__label">
        <input
          value={name || ""}
          onChange={handleNameChange}
          name="name"
          id="profile"
          type="text"
          className="popup__input popup__input_type_name"
          placeholder="Введите имя"
          minLength={2}
          maxLength={20}
          required=""
        />
        <span id="profile-error" className="error" />
      </label>
      <label className="popup__label">
        <input
          value={description || ""}
          onChange={handleDescriptionChange}
          name="about"
          id="speciality"
          type="text"
          className="popup__input popup__input_type_speciality"
          placeholder="Введите специальность"
          minLength={2}
          maxLength={200}
          required=""
        />
        <span id="speciality-error" className="error" />
      </label>
    </PopupWithForm>
  );
};

export default EditProfilePopup;
