class AuthApi {
  constructor(baseUrl) {
    this._url = baseUrl;
  }

  _request(baseUrl) {
    return fetch(baseUrl).then(this._checkResponse);
  }

  _checkResponse(res) {
    if (res.ok) {
      return res.json();
    }
    return Promise.reject(`Ошибка: ${res.status}`);
  }

  login(email, password) {
    return this._request(`${this._url}signup`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json" 
      },
      body: JSON.stringify({
        email,
        password
      })
    })
  }

  addCard(cardData) {
    return this._request(`${this._url}/cards`, {
      method: "POST",
      headers: this._headers,
      body: JSON.stringify({
        name: cardData.name,
        link: cardData.link,
      }),
    });
  }

}

const authApi = new AuthApi("https://mesto.nomoreparties.co/");

export default authApi;
